function mapAlert(alert) {
    if(alert){
        return "Alert";
    }
    else{
        return "";
    }
}
function mapTotalFailure(fault) {
    if(fault){
        return "Total Failure";
    }
    else{
        return "";
    }
}
function mapSelector(selector) {
    switch(selector){
        case 0: 
            return "OFF";
        case 1:
            return "ON";
        case 2:
            return "REM";
        default:
            return "";
    }
}
function mapPump(pump) {
    if(pump){
        return "ON";
    }
    else{
        return "OFF";
    }
}

